mapper.select:
SELECT COUNT(*)
FROM walk_crew_member
WHERE memberId = #{memberId}
AND crewId =
#{crewId}

mapper.delete:
DELETE FROM
walk_crew_member
WHERE crewId = #{crewId} AND memberId = #{memberId}

mapper.update:
UPDATE walk_crew_member
SET role = #{role}
WHERE
memberId = #{memberId} AND crewId = #{crewId}


UPDATE walk_crew_member
SET
status = 'APPROVED'
WHERE crewId = #{crewId}
AND memberId = #{memberId}

mapper.select:
SELECT EXISTS (
SELECT 1
FROM
walk_crew_member
WHERE crewid = #{crewId} AND memberid = #{memberId}
)
